mapper.update:
        update  SYS_USER
        set
				headSculpturePath = #{headSculpturePath}
        where
		userId= #{userId}

        update  SYS_USER
        set
				businessId = #{businessId}
        where
		userId= #{userId}

mapper.select:
SELECT *
FROM sys_user
WHERE 1=1
AND loginId = #{loginId}
AND password = #{password}
