mapper.select:
SELECT ( @i := CASE WHEN @pre_publishtime = hairstyle.publishtime THEN @i + 1 ELSE 1 END ) rownum, date_format( hairstyle.publishtime, '%Y%m%d' ) publishtime, hairstyle.styletitle, hairstyle.content, hairstyle.srcImagePath, ( @pre_publishtime := hairstyle.publishtime ) AS publishtimea, hairstyle.ID AS hairstyleId, hairstyle.userName, hairstyle.evaluatecount, date_format( hairstyle.publishdateteime, '%Y%m%d%H%i%s' ) publishdateteime FROM ( SELECT subhairstyle.*, subSYS_USER.userName FROM bus_hairstyle subhairstyle, SYS_USER subSYS_USER WHERE subSYS_USER.userId = subhairstyle.userid
AND subSYS_USER.loginId = #{pd.loginId}
ORDER BY subhairstyle.publishdateteime DESC ) hairstyle, ( SELECT @i := 0 ,@pre_publishtime := '' ) AS a GROUP BY hairstyle.publishtime, hairstyle.publishdateteime, hairstyle.styletitle, hairstyle.content, hairstyle.srcImagePath, hairstyle.ID, hairstyle.userName, hairstyle.evaluatecount ORDER BY hairstyle.publishdateteime DESC

SELECT ( @i := CASE WHEN @pre_publishtime = hairstyle.publishtime THEN @i + 1 ELSE 1 END ) rownum, date_format( hairstyle.publishtime, '%Y%m%d' ) publishtime, hairstyle.styletitle, hairstyle.content, hairstyle.srcImagePath, ( @pre_publishtime := hairstyle.publishtime ) AS publishtimea, hairstyle.ID AS hairstyleId, hairstyle.userName, hairstyle.evaluatecount, date_format( hairstyle.publishdateteime, '%Y%m%d%H%i%s' ) publishdateteime FROM ( SELECT subhairstyle.*, subSYS_USER.userName FROM bus_hairstyle subhairstyle, SYS_USER subSYS_USER WHERE subSYS_USER.userId = subhairstyle.FxsUserId
AND subSYS_USER.userId = #{pd.userId}
ORDER BY subhairstyle.publishdateteime DESC ) hairstyle, ( SELECT @i := 0 ,@pre_publishtime := '' ) AS a GROUP BY hairstyle.publishtime, hairstyle.publishdateteime, hairstyle.styletitle, hairstyle.content, hairstyle.srcImagePath, hairstyle.ID, hairstyle.userName, hairstyle.evaluatecount ORDER BY hairstyle.publishdateteime DESC

SELECT ( @i := CASE WHEN @pre_publishtime = hairstyle.publishtime THEN @i + 1 ELSE 1 END ) rownum, date_format( hairstyle.publishtime, '%Y%m%d' ) publishtime, hairstyle.styletitle, hairstyle.content, hairstyle.srcImagePath, ( @pre_publishtime := hairstyle.publishtime ) AS publishtimea, hairstyle.ID AS hairstyleId, hairstyle.userName, hairstyle.evaluatecount, date_format( hairstyle.publishdateteime, '%Y%m%d%H%i%s' ) publishdateteime, hairstyle.hairstyleevaluateId FROM ( SELECT hairstylelist.*, hairstyleevaluate.ID AS hairstyleevaluateId FROM ( SELECT subbus_hairstyle.*, subSYS_USER.userName FROM bus_hairstyle subbus_hairstyle, SYS_USER subSYS_USER WHERE subSYS_USER.userId = subbus_hairstyle.FxsUserId AND subSYS_USER.userId IN ( SELECT focu.FXSID FROM bus_focusonrecords focu WHERE 1 = 1
and focu.USERID = #{pd.userId}
) ORDER BY subbus_hairstyle.publishdateteime DESC ) hairstylelist LEFT JOIN bus_hairstyleevaluate hairstyleevaluate ON hairstyleevaluate.hairstyleid = hairstylelist.ID
AND hairstyleevaluate.userid = #{pd.userId}
) hairstyle, ( SELECT @i := 0 ,@pre_publishtime := '' ) AS a GROUP BY hairstyle.publishtime, hairstyle.publishdateteime, hairstyle.styletitle, hairstyle.content, hairstyle.srcImagePath, hairstyle.ID, hairstyle.userName, hairstyle.evaluatecount, hairstyle.hairstyleevaluateId ORDER BY hairstyle.publishdateteime DESC
